<?php

namespace Tests\Unit\Requests\Drink;

use App\Http\Requests\API\Drink\PostAddRequest;
use Illuminate\Support\Facades\Validator;
use Illuminate\Support\Str;
use PHPUnit\Framework\TestCase;

class PostAddRequestTest extends TestCase
{

    /**
     * TODO add からstoreに変わった際には修正が必要
     * バリデーションテスト
     *
     * @param 項目名
     * @param 値
     * @param 期待値
     * @dataProvider dataprovider
     */
    public function test_post_Add_Request(array $items, array $values, bool $expect)
    {
        $request = new PostAddRequest();
        $rules = $request->rules();
        $dataList = array_combine($items, $values);

        $validator = Validator::make($dataList, $rules);
        $result = $validator->passes();
        $this->assertEquals($expect, $result);

    }

    /**
     * データプロバイダ
     * validationエラー確認用
     *
     * @return dataProvider
     *
     */
    public function dataprovider(): array
    {
        $maxLengthComment = Str::random(255);
        $overLimitComment = Str::random(256);

        return [
            'png形式OK' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/png;base64,', '3', 'テストメッセージを送信しました。'],
                true
            ],
            'svg形式OK' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/svg;base64,', '3', 'テストメッセージを送信しました。'],
                true
            ],
            'jpg形式OK' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/jpg;base64,', '3', 'テストメッセージを送信しました。'],
                true
            ],
            '投稿画像未入力エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['', '3', 'テストメッセージを送信しました。'],
                false
            ],
            '投稿画像csv形式エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/csv;base64,', '3', 'テストメッセージを送信しました。'],
                false
            ],
            '投稿画像pdf形式エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/pdf;base64,', '3', 'テストメッセージを送信しました。'],
                false
            ],
            '投稿画像pdf形式エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/md;base64,', '3', 'テストメッセージを送信しました。'],
                false
            ],
            'タグ未入力エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/png;base64,', '', 'テストメッセージを送信しました。'],
                false
            ],
            'タグ形式エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/svg;base64,', '日本酒', 'テストメッセージを送信しました。'],
                false
            ],
            'タグテーブルに存在しない値エラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/jpg;base64,', '999', 'テストメッセージを送信しました。'],
                false
            ],
            'コメント最大入力値OK' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/jpg;base64,', '3', $maxLengthComment],
                true
            ],
            'コメント最大入力値超えるエラー' => [
                ['post_image', 'post_tag', 'comment'],
                ['data:image/jpg;base64,', '3', $overLimitComment],
                false
            ],

        ];
    }
}
